import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Products from "./Products";

function Productslist()
{
    const api_prod = "https://fakestoreapi.com/products";
    const [products,setProducts] = useState([]);
    const [categories,setCategories] = useState([]);

    const getProducts = () => {
        fetch(api_prod)
        .then((res) => res.json())
        .then((data) => setProducts(data));
    }

    const getCategories = () => {
        fetch(`${api_prod}/categories`)
        .then((res) => res.json())
        .then((data) => setProducts(data));
    }

    useEffect(() => {
        getProducts()
        getCategories()
    },[]);

    return(
    <>
    <h2 className="text center p-4"> What we offer </h2>
    <div className="container">
        {
            categories.map((cat) =>
                {
                    return <button className="btn-btn-info">{}</button>
                })
        }
    <div className="row">
        {products.map((product) => {
            return(
            <div className="col-4" key={product.id}>
                <Products product={product} showButton={true}/>
            </div>
            );
        })}

        {/*<div className="col-4">
            <Products/>
        </div>

        <div className="col-4">
            <Products/>
        </div>

        <div className="col-4">
            <Products/>
        </div>

        <div className="col-4">
            <Products/>
        </div>*/}
    </div>
    </div>
    </>
    );
}

export default Productslist
