import {useEffect, useState} from "react";
import Products from "./Products";

function Productslist()
{
    const api_prod = "https://fakestoreapi.com/products";
    const [products,setProducts] = useState([]);

    useEffect(() => {
        fetch(api_prod)
        .then((res) => res.json())
        .then((data) => setProducts(data));
    },[]);

    return(
    <>
    <h2 className="text center p-4"> What we offer </h2>
    <div className="container">
    <div className="row">
        {product.map(() => {
            return(<div className="col-4">
            <Products/>
        </div>)
        })}
        <div className="col-4">
            <Products/>
        </div>

        <div className="col-4">
            <Products/>
        </div>

        <div className="col-4">
            <Products/>
        </div>

        <div className="col-4">
            <Products/>
        </div>
    </div>
    </div>
    </>
    );
}

export default Productslist
